import React from 'react';
import { Jumbotron, Button } from 'reactstrap';

let JumboDisplay = (props) => {
    let styles = {
        height: "100vh"
    }

    return( <Jumbotron style={styles}>
        <h1 className="display-3">{props.count}</h1>
        <p className="lead">Counting above...</p>
        <hr className="my-2" />
        <p className="lead">
          <Button id="decrease" color="warning" onClick = {props.setCounter}> -1 </Button>
          <Button id="increase" color="success" onClick = {props.setCounter}> +1 </Button>
        </p>
        <Button color="danger" size="lg" block onClick={props.exitHandler}>EXIT</Button>
     </Jumbotron>);
} 

export default JumboDisplay;
